<?php	// Header Banner Section	$wp_customize -> add_section(		'header_cta_slider',		array (			'title'							=> __('Header CTA Slider'),			'description'				=> __(''),			'priority'					=> 10,			//'active_callback'		=> function() { return is_page_template('page-templates/blog-template.php');}		));	// Add Header Banner Title One Setting	$wp_customize -> add_setting ( 'title_one',		array(			'default'							=> __(''),			'type'								=> 'theme_mod',			'sanitize_callback'		=> 'sanitize_text_field',			'transport'						=> 'postMessage',		));	// Add Header Banner Title One Control	$wp_customize -> add_control (		new WP_Customize_Control (			$wp_customize,			'title_one',			array (				'label'				=> __( 'Slider Title &#35;1', 'the1-darkside' ),				'section'			=> 'header_cta_slider',				'settings'		=> 'title_one',				'type'				=> 'text',				'description'	=> __('Add CTA'),				'priority' 		=> 10,			)));	// Header Banner Title One Selective Refresh	$wp_customize->selective_refresh->add_partial('title_one',			array(				'selector' 				=> '#cta-slider1-refresh',				'settings' 				=> 'title_one',				'render_callback' => wp_get_attachment_image(get_theme_mod('title_one'), '')			));	// Header Banner Title One Link Setting	$wp_customize->add_setting( 'title_one_link',			array(				'default' 					=> __(''),				'type' 							=> 'theme_mod',				'capability' 				=> 'edit_theme_options',				'transport' 				=> 'refresh',				'sanitize_callback' => 'esc_url',			));	// Header Banner Title One Link Control	$wp_customize->add_control( 'title_one_link',			array(				'type' 				=> 'url',				'priority' 		=> 10,				'section' 		=> 'header_cta_slider',				'label' 			=> __( 'Slider Title &#35;1 Link', 'the1-darkside' ),				'description' => __('URL for Slider Title &#35;1'),			));	// Add Header Banner Title Two Setting	$wp_customize -> add_setting ( 'title_two',			array(				'default'							=> __(''),				'type'								=> 'theme_mod',				'sanitize_c